        public static LanguageConfig getLanguageConfigByCode(string code)
        {
            LanguageConfig result = new LanguageConfig();
            for (int i = 0; i < arrList.Count; i++)
            {
                if (arrList[i].Code == code)
                {
                    result = arrList[i];
                    break;
                }
            }
            return result;
        }

        public static LanguageConfig getLanguageConfigByCode(string code,string lang)
        {
            LanguageConfig result = new LanguageConfig();
            for (int i = 0; i < arrList.Count; i++)
            {
                if (arrList[i].Code == code)
                {
                    if (lang == "en")
                    {
                        arrList[i].Name = arrList[i].Name2;
                    }
                    else
                    {
                        arrList[i].Name = arrList[i].Description;
                    }
                    result = arrList[i];
                    break;
                }
            }
            return result;
        }
    }
